% avg_plot:  This will take the avg_1, avg_2 avg_3 arrays and plot them.% written by:  Jonathan Jacobs%              October 1995 - April 1996 (last mod: 04/04/96)function avg_plotglobal samp_freq avg_1 avg_2 avg_3 namelist debugmefigure;%orient landscape;% look for the first non-zero entry in the list.% this is where the std data really begins% we must remember to subtract off the '20' we added in TPLOT3 and UPDATE3% to allow us to look at SD, avg at time of saccade and before.t_vect = 1000*((1:length(avg_1))-20)/samp_freq;good_pts = find(~isnan(avg_1));subplot(2,1,1)orient landscapeplot (t_vect(good_pts), avg_1(good_pts))ylabel( 'Avg pos (deg)' )%if ~ exist('what_f_names')%   title(namelist)% else%   title(what_f_names)%endsubplot(2,1,2)plot (t_vect(good_pts), avg_2(good_pts))ylabel( 'Avg vel (deg/s)' )xlabel('time before saccade (ms)')%subplot(3,1,3)%plot (t_vect(good_pts), avg_3(good_pts))%ylabel( 'Avg accel (deg/s^2)' )if ~exist('debugme') debugme = 0; endif ~debugme   clear start_pt end_pt t_vectend