% drawme.m:    called by showme.m to do the plotting.% written by:  Jonathan Jacobs%              November 1995 - May 1996  (last mod: 05/25/96)mkSize = 4;when = offset * 1000/samp_freq;  %convert sample index to timeif (t_or_s == 't')   x_vect = (1/samp_freq:1/samp_freq:num_d_pts/samp_freq); else   x_vect = (1:num_d_pts);endif p_or_v == 'b'   subplot(2,1,1);endif (p_or_v == 'p') | (p_or_v == 'b')   plot(x_vect(1:num_d_pts), pos(1:num_d_pts))   hold on%  plot( x_vect(sacp-offset), pos(sacp-offset), 'ro', 'Marker', 5)   plot( x_vect(sacv-offset), pos(sacv-offset), 'ro', 'Marker', mkSize)   plot( x_vect(sacpon), pos(sacpon), 'co', 'Marker', mkSize)   plot( x_vect(sacpoff), pos(sacpoff), 'go', 'Marker', mkSize)   hold off   ylabel( 'eye position (deg)' ) elseif p_or_v == 'v'   plot(x_vect(1:num_d_pts), vel(1:num_d_pts))   hold on   plot( x_vect(sacv-offset), vel(sacv-offset), 'ro', 'Marker', mkSize)   plot( x_vect(sacvon), vel(sacvon), 'co', 'Marker', mkSize)   plot( x_vect(sacvoff), vel(sacvoff), 'go', 'Marker', mkSize)   hold off   ylabel( 'eye velocity (deg/sec)' )endtitle( [what_f_names ' at  ' num2str(when) 'ms before saccades'...                                               ' (vel-derived onset)'] )if (t_or_s == 't')   xlabel( 'time (seconds)' ) else   xlabel( 'sample number')endif p_or_v == 'b'   subplot(2,1,2)   plot( x_vect(1:num_d_pts), vel(1:num_d_pts) )   hold on   plot( x_vect(sacv-offset), vel(sacv-offset), 'ro', 'Marker', mkSize)   plot( x_vect(sacvon), vel(sacvon), 'co', 'Marker', mkSize)   plot( x_vect(sacvoff), vel(sacvoff), 'go', 'Marker', mkSize)   hold off   ylabel( 'eye velocity (deg/s)' )endif get(hBox, 'value') == 1           % draw foveal/slip radius   if (p_or_v == 'b')      subplot(2,1,1)      fovrad;      subplot(2,1,2)      sliprad;    elseif (p_or_v) == 'p'      fovrad;    elseif (p_or_v) == 'v'      sliprad;   endend