% ergpath.m: add the directories needed to use the% mfERG tools analysis m-files.% Written by:  Jonathan Jacobs%              June 2008  (last mod: 06/06/08)function ergath()global OMroot[comp, maxsize] = computer;vers=version;P = path;disp( 'Setting mfERG paths...' )if comp(1) == 'M' & vers(1)<='5' % 'classic' Mac OS   sep  = ':';                   % between dirs in a single path   sep2 = ';';                   % between path entries elseif comp(1) == 'P'           % PC   sep  = '\';    sep2 = ';'; else                            % Unix (including Mac OS X)   sep  = '/';   sep2 = ':';    end% generally 'OMroot' will just be the MATLAB directory,% but you can change it to whatever suits your needs.% This could be useful if you need to run MATLAB 4 and MATLAB 5% and only want to have one OMtools installation.  % find the root directory for the data files.cd([OMroot sep 'ergtools'])add = '';add = [add OMroot sep 'ergtools' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'DataFun' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'Exp' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'FreqAnalysis' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'HexMap' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'Peak' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'Smooth' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'util' sep2];add = [add OMroot sep 'ergtools' sep 'JL_Toolboxv2' sep 'window' sep2];add = [add OMroot sep 'ergtools' sep 'MfocProgramv2' sep2];add = [add OMroot sep 'ergtools' sep 'MfocProgramv2' sep 'SupportRoutines' sep2];add = [add OMroot sep 'ergtools' sep 'MfocProgramv2' sep 'Temp' sep2];add = [add OMroot sep 'ergtools' sep 'MfocProgramv2' sep 'Util' sep2];add = [add OMroot sep 'ergtools' sep 'V2_01_Run' sep2];path( P, add );eval( ['cd ' '''' matlabroot '''' ;] )