function plthglobal rh lh st datanameplthFigH = figure;plthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';endif ~isempty(rh)   t=maket(rh); elseif ~isempty(lh)   t=maket(lh); else   disp('No horizontal data!?!')   returnend maxpt=max(max([lh,rh]));minpt=min(min([lh,rh]));maxt=max(t);hold onif ~isempty(lh)   lhH=plot(t,lh,lhColor);   lhH.DisplayName='lh';   lhtxtH=text(maxt,minpt,'LH');   lhtxtH.Color=lhColor;end   if ~isempty(rh)   rhH=plot(t,rh,rhColor);   rhH.DisplayName='rh';   rhtxtH=text(maxt,maxpt,'RH');   rhtxtH.Color=rhColor;end   if ~isempty(st)   stH=plot(t,st,'r');   stH.DisplayName='st';   sttxtH=text(maxt,maxpt,'Hor Stim');   sttxtH.Color='r';end   if exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendept; %dragger