function plthvglobal rh rv lh lv st sv datanameplthFigH = figure;plthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';end%lvColor = [.7 .5 0];lvColor = lhColor;rvColor = rhColor;novrt=0;if ~isempty(rv)   t=maket(rv); elseif ~isempty(lv)   t=maket(lv); else   disp('No vertical data!?!')   plth   returnendmaxpt=max(max([lh,rh,lv,rv]));minpt=min(min([lh,rh,lv,rv]));maxt=max(t);hold onif ~isempty(lh)   lhH=plot(t,lh,lhColor);   lhH.DisplayName='lh';   lhtxtH=text(maxt,minpt,'LH');   lhtxtH.Color=lhColor;end   if ~isempty(rh)   rhH=plot(t,rh,rhColor);   rhH.DisplayName='rh';   rhtxtH=text(maxt,maxpt,'RH');   rhtxtH.Color=rhColor;end   if ~isempty(st)   stH=plot(t,st,'r');   stH.DisplayName='st';   sttxtH=text(maxt,maxpt,'Hor Stim');   sttxtH.Color='r';end  if ~isempty(lv)   lvH=plot(t,lv,lvColor);   lvH.DisplayName='lv';   lvtxtH=text(maxt,minpt,'LV');   lvtxtH.Color=lvColor;end   if ~isempty(rv)   rvH=plot(t,rv,rvColor);   rvH.DisplayName='rv';   rvtxtH=text(maxt,maxpt,'RV');   rvtxtH.Color=rvColor;end   if ~isempty(sv)   svH=plot(t,sv,'r');   svH.DisplayName='sv';   svtxtH=text(maxt,maxpt,'Vert Stim');   svtxtH.Color='r';end   if exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endend   ept; %dragger%clear rehH lehH revH levH%clear tempFigH tempFigColor lhColor lvColor rhColor rvColor