% omstart.m:   execute when MATLAB starts up.%                1) Run 'OMpath' to add OMtools directories to path%                2) Move to OMtools main directory%% This file should be in the same folder as findomtools.m, setomtools.m,% and ompath.m.% Written by:  Jonathan Jacobs%              March 2017  (last mod: 03/09/17)function omstart( ~ )[omtoolspath,ot_ver] = findomtools;if isempty(omtoolspath)   disp('No OMtools installation found.')   disp('Try running install_omtools.')   returnend%setomtools(omtoolspath, ot_ver);omlab='http://omlab.org/software/';pcloud='https://u.pcloud.link/publink/show?code=kZAxKv7ZHJLiuFoy1e7jFwX93jXr4fo3x9Y7';cd(omtoolspath)cd('omtoolsdirs')fprintf('Checking OMtools...\n')ompathfprintf('Installed in:  %s\n',omtoolspath)fprintf('OMtools date:  %s.  ',ot_ver)disp(['Check <a href= "' omlab '">OMlab.org</a> or <a href="' ...   pcloud '">pCloud</a> for updates.'])omprefpath = findomprefs;temp=find(omprefpath==filesep);ompp = omprefpath(1:temp(end));fprintf('OMtools prefs: %s\n', omprefpath)ztprefpath = findztprefs;temp=find(ztprefpath==filesep);ztpp = ztprefpath(1:temp(end));if ~strcmpi(ompp,ztpp)   disp(['zoomtool prefs:  ' ztprefpath])enddatarootpath = dataroot('r');disp(['Data folder:   ' datarootpath ...   '  --  To change, type dataroot(''w'')'])%disp(' ')disp('Ready for use.')cd(datarootpath)