% digstimplot.m: plot the digital stim information%% written by: Jonathan Jacobs%             November 2001  (last mod: 11/08/01)%hold onaa = length(t_stim)*2-1;xx(1:2:aa)   = t_stim/1000;         % odd indicesxx(2:2:aa-1) = t_stim(2:end)/1000;  % even indicesyy(1:2:aa)   = whichLED;yy(2:2:aa-1) = whichLED(1:end-1);figuredp=plot(xx,LEDangles(yy),'md-');set(dp,'markerfacecolor','g')set(dp,'markeredgecolor','w')set(dp,'MarkerSize',4)set(dp,'LineWidth',0.75)