function [ret,x0,str,ts,xts]=kltest(t,x,u,flag);%KLTEST	is the M-file description of the SIMULINK system named KLTEST.%	The block-diagram can be displayed by typing: KLTEST.%%	SYS=KLTEST(T,X,U,FLAG) returns depending on FLAG certain%	system values given time point, T, current state vector, X,%	and input vector, U.%	FLAG is used to indicate the type of output to be returned in SYS.%%	Setting FLAG=1 causes KLTEST to return state derivatives, FLAG=2%	discrete states, FLAG=3 system outputs and FLAG=4 next sample%	time. For more information and other options see SFUNC.%%	Calling KLTEST with a FLAG of zero:%	[SIZES]=KLTEST([],[],[],0),  returns a vector, SIZES, which%	contains the sizes of the state vector and other parameters.%		SIZES(1) number of states%		SIZES(2) number of discrete states%		SIZES(3) number of outputs%		SIZES(4) number of inputs%		SIZES(5) number of roots (currently unsupported)%		SIZES(6) direct feedthrough flag%		SIZES(7) number of sample times%%	For the definition of other parameters in SIZES, see SFUNC.%	See also, TRIM, LINMOD, LINSIM, EULER, RK23, RK45, ADAMS, GEAR.% Note: This M-file is only used for saving graphical information;%       after the model is loaded into memory an internal model%       representation is used.% the system will take on the name of this mfile:sys = mfilename;new_system(sys)simver(1.3)if (0 == (nargin + nargout))     set_param(sys,'Location',[65,164,554,514])     open_system(sys)end;set_param(sys,'algorithm',     'RK-45')set_param(sys,'Start time',    '0')set_param(sys,'Stop time',     '10')set_param(sys,'Min step size', '0.001')set_param(sys,'Max step size', '0.005')set_param(sys,'Relative error','1e-3')set_param(sys,'Return vars',   '')add_block('built-in/Sum',[sys,'/','Sum'])set_param([sys,'/','Sum'],...		'hide name',0,...		'inputs','+-',...		'position',[85,44,105,66])add_block('built-in/Step Fcn',[sys,'/','Step Input'])set_param([sys,'/','Step Input'],...		'hide name',0,...		'Time','5',...		'After','40',...		'position',[30,65,50,85])add_block('built-in/Step Fcn',[sys,'/','Step Input1'])set_param([sys,'/','Step Input1'],...		'hide name',0,...		'Time','0.5',...		'After','40',...		'position',[30,30,50,50])add_block('built-in/Constant',[sys,'/',['positive --> Step',13,'neg --> Sig Gen']])set_param([sys,'/',['positive --> Step',13,'neg --> Sig Gen']],...		'Value','-1',...		'position',[35,128,75,152])add_block('built-in/Switch',[sys,'/','Switch'])set_param([sys,'/','Switch'],...		'position',[160,124,190,156])add_block('built-in/Signal Generator',[sys,'/',['Signal',13,'Generator']])set_param([sys,'/',['Signal',13,'Generator']],...		'Peak','25.000000',...		'Peak Range','50.000000',...		'Freq','6.280000',...		'Freq Range','10.000000',...		'Wave','Sqr',...		'Units','Rads',...		'position',[70,193,115,227])%     Subsystem  ['eye,tgt',13,'vel scope'].new_system([sys,'/',['eye,tgt',13,'vel scope']])set_param([sys,'/',['eye,tgt',13,'vel scope']],'Location',[0,59,274,252])add_block('built-in/S-Function',[sys,'/',['eye,tgt',13,'vel scope/S-function',13,'M-file which plots',13,'lines',13,'']])set_param([sys,'/',['eye,tgt',13,'vel scope/S-function',13,'M-file which plots',13,'lines',13,'']],...		'function name','sfunyst',...		'parameters','ax, color, npts, dt',...		'position',[130,55,180,75])add_block('built-in/Inport',[sys,'/',['eye,tgt',13,'vel scope/x']])set_param([sys,'/',['eye,tgt',13,'vel scope/x']],...		'position',[65,55,85,75])add_line([sys,'/',['eye,tgt',13,'vel scope']],[90,65;125,65])set_param([sys,'/',['eye,tgt',13,'vel scope']],...		'Mask Display','plot(0,0,100,100,[83,76,63,52,42,38,28,16,11,84,11,11,11,90,90,11],[75,58,47,54,72,80,84,74,65,65,65,90,40,40,90,90])',...		'Mask Type','Storage scope.')set_param([sys,'/',['eye,tgt',13,'vel scope']],...		'Mask Dialogue','Storage scope using MATLAB graph window.\nEnter plotting ranges and line type.|Initial Time Range:|Initial y-min:|Initial y-max:|Storage pts.:|Line type (rgbw-.:xo):')set_param([sys,'/',['eye,tgt',13,'vel scope']],...		'Mask Translate','npts = @4; color = @5; ax = [0, @1, @2, @3]; dt=-1;')set_param([sys,'/',['eye,tgt',13,'vel scope']],...		'Mask Help','This block uses a MATLAB figure window to plot the input signal.  The graph limits are automatically scaled to the min and max values of the signal stored in the scope''s signal buffer.  Line type must be in quotes.  See the M-file sfunyst.m.')set_param([sys,'/',['eye,tgt',13,'vel scope']],...		'Mask Entries','10\/-35\/35\/5000\/''y-/g--/c-./w:/m*/ro/b+''\/')%     Finished composite block ['eye,tgt',13,'vel scope'].set_param([sys,'/',['eye,tgt',13,'vel scope']],...		'position',[425,130,455,170])add_block('built-in/Mux',[sys,'/','Mux'])set_param([sys,'/','Mux'],...		'inputs','2',...		'position',[355,131,385,164])add_line(sys,[390,150;420,150])add_line(sys,[310,140;350,140])add_line(sys,[120,210;125,210;125,150;155,150])add_line(sys,[80,140;155,140])add_line(sys,[55,40;65,40;65,50;80,50])add_line(sys,[55,75;65,75;65,60;80,60])add_line(sys,[110,55;135,55;135,130;155,130])add_line(sys,[195,140;255,140])add_line(sys,[215,140;215,250;340,250;350,155])drawnow% Return any arguments.if (nargin | nargout)	% Must use feval here to access system in memory	if (nargin > 3)		if (flag == 0)			eval(['[ret,x0,str,ts,xts]=',sys,'(t,x,u,flag);'])		else			eval(['ret =', sys,'(t,x,u,flag);'])		end	else		[ret,x0,str,ts,xts] = feval(sys);	endelse	drawnow % Flash up the model and execute load callbackend